import { Component, OnInit } from '@angular/core';
import { Router } from '@angular/router';
import { ToastrService } from 'ngx-toastr';
import { CommonService } from 'src/app/common-service/common.service';

@Component({
  selector: 'app-customer-dashboard',
  templateUrl: './customer-dashboard.component.html',
  styleUrls: ['./customer-dashboard.component.scss']
})
export class CustomerDashboardComponent implements OnInit {

  // Public Variable
  public watchesProductData: any = [
    { id: '1', productName: 'Titan', productImage: 'https://assets.myntassets.com/h_1440,q_100,w_1080/v1/assets/images/757863/2023/2/23/9182be59-04df-49b2-a60e-eecbfeddba311677143283050TitanMenGold-TonedDialWatchNF1578YM051.jpg', productPrice: '100', productQty: 0 },
    { id: '2', productName: 'Fastrack', productImage: 'data:image/jpeg;base64,', productPrice: '500', productQty: 0 },
    { id: '3', productName: 'Sonata', productImage: 'data:image/jpeg;base64,', productPrice: '900', productQty: 0 },
  ];

  public laptopProductData: any = [
    { id: '1', productImage: 'data:image/jpeg;base64,', productName: 'Apple', productPrice: '2000', productQty: 0 },
    { id: '2', productImage: 'data:image/jpeg;base64,', productName: 'HP', productPrice: '3000', productQty: 0 },
    { id: '3', productImage: 'data:image/jpeg;base64,', productName: 'Dell', productPrice: '4000', productQty: 0 },
  ];

  public tvProductData: any = [
    { id: '1', productImage: 'data:image/jpeg;base64,', productName: 'Sony', productPrice: '10000', productQty: 0 },
    { id: '2', productImage: 'data:image/jpeg;base64,', productName: 'Lg', productPrice: '30000', productQty: 0 },
    { id: '3', productImage: 'https://encrypted-tbn0.gstatic.com/images?q=tbn:ANd9GcSNA4iAMYy-enrIUHU_SUKL1j7aVJoyKXTBKNSSqLU5fg&usqp=CAU&ec=48600112', productName: 'Mi', productPrice: '50000', productQty: 0 },
  ];

  public phoneProductData: any = [
    { id: '1', productImage: 'https://encrypted-tbn0.gstatic.com/images?q=tbn:ANd9GcTY6v6p967FbTNj9MBd12C1BjwdWrDkdHu2xkWaoVI_nKYFoUZcCmrCeBMZYBozCwyZtU-iEopSKo0&usqp=CAU&ec=48600112', productName: 'Samsung', productPrice: '100', productQty: 0 },
    { id: '2', productImage: 'data:image/jpeg;base64,', productName: 'Apple', productPrice: '600', productQty: 0 },
    { id: '3', productImage: 'data:image/jpeg;base64,', productName: 'Mi', productPrice: '200', productQty: 0 },
  ];

  public currentPage: any;

  public addFlage: boolean = false;

  public searchProduct: any = [];

  public searchProductFlage: boolean = false;

  public appProductsData: any = [...this.watchesProductData, ...this.laptopProductData, ...this.tvProductData, ...this.phoneProductData];


  constructor(private router: Router, private toastr: ToastrService, private commonService: CommonService) {
    setTimeout(() => {
      this.addFlage = true;
    }, 2000);
  }

  ngOnInit(): void {
    localStorage.setItem('productData', JSON.stringify(this.appProductsData));
    this.productAlreadyAdded();
    this.commonService.searchProduct.subscribe((res) => {
      if (res === '') {
        this.searchProduct = [];
        this.searchProductFlage = false;
      } else {
        this.searchProduct = this.appProductsData.filter((element: any) => {
          return element.productName.trim().toLowerCase().includes(res.trim().toLowerCase());
        });
        this.searchProductFlage = true;
      }
    });
  }

  productAlreadyAdded() {
    let cartProducatDetials = JSON.parse(localStorage.getItem('cartProducatDetials') as any) ? JSON.parse(localStorage.getItem('cartProducatDetials') as any) : [];

    if (cartProducatDetials.length >= 1) {
      let productData = JSON.parse(localStorage.getItem('productData') as any);

      for (let i = 0; i < productData?.length; i++) {
        let isUserSelected = false;
        for (let j = 0; j < cartProducatDetials?.length; j++) {
          if (productData[i]?.productImage == productData[j]?.productImage) {
            isUserSelected = true;
            productData[i]['addToCart'] = true;
            break;
          }
        }
        if (isUserSelected == false) {
          productData[i]['addToCart'] = false;
        }
      }
      localStorage.setItem('productData', JSON.stringify(productData));
      this.appProductsData = JSON.parse(localStorage.getItem('productData') as any);
    }
  }

  productDetails(productData: any) {
    this.router.navigate(
      ['dashboard/product-detail'],
      { queryParams: { productName: `${productData.productName}`, imageParth: `${productData.productImage}` } }
    );
  }

  pageChange(event: any) {
    this.currentPage = event;
  }

  addToCart(productDetails: any) {
    let loggedInUserData = JSON.parse(localStorage.getItem('loggedInUser') as any);
    if (loggedInUserData === null) {
      this.toastr.error('User is not register', 'Error', {
        timeOut: 2000,
      });
      setTimeout(() => {
        this.router.navigate(['/authentication/registration']);
      }, 1000);
    }
    else {
      // Get cartProducatDetials from localStorage
      let cartProducatDetials = JSON.parse(localStorage.getItem('cartProducatDetials') as any);

      if (cartProducatDetials) {
        // find this product new or added in cart
        let findCartProduct = cartProducatDetials.find((res: any) => {
          return res.productName === productDetails.productName && res.productImage === productDetails.productImage
        });

        if (findCartProduct) {
          this.router.navigate(['cart']);
        } else {
          // New product Qty add and navigate to cart page
          productDetails.productQty = ++productDetails.productQty;
          productDetails.productTotalPrice = +productDetails.productPrice;
          cartProducatDetials.push(productDetails);
          localStorage.setItem('cartProducatDetials', JSON.stringify(cartProducatDetials));
          this.toastr.success('Product add in cart successfully', 'Success', {
            timeOut: 2000,
          });
          this.router.navigate(['cart']);
        }
      } else {
        // New product Qty add and navigate to cart page
        productDetails.productQty = ++productDetails.productQty;
        productDetails.productTotalPrice = +productDetails.productPrice;
        let productDetials = [];
        productDetials.push(productDetails);

        localStorage.setItem('cartProducatDetials', JSON.stringify(productDetials));
        this.toastr.success('Product add in cart successfully', 'Success', {
          timeOut: 2000,
        });
        this.router.navigate(['cart']);
      }
    }
  }

}
